building_function.$fn:
function(errs, template, curr_obj, all_templates, root_obj, hierarchy, rows, cols) {
  curr_obj.harvest_technology_name_or_id = "/app/aleph2/library/logstash_harvester.jar";
  curr_obj.multi_node_enabled = false;
  curr_obj.lock_to_nodes = true;

  var ls_obj = {};
  ls_obj.script = template.element.form_model.script;
  ls_obj.output_override = template.element.form_model.output_override;
  ls_obj.debug_verbosity = template.element.form_model.debug_verbosity;
  curr_obj.harvest_configs = [ { config: ls_obj } ];
}
